import { combineReducers } from 'redux';

const songsReducer = () => {
  return [
    { title: 'Stand Up In It',
      description: '',
      url: 'https://youtu.be/_StBCnlB2DQ',
      duration: '1:53'
    },
    {
      title: 'A Woman Needs To Be Loved',
      description: '',
      url: 'https://youtu.be/Nulp3zR3_BA',
      duration: '5:07'
    },
    {
      title: 'Summer Tour Live Report',
      description: '',
      url: 'https://youtu.be/pmRh43QEuM8',
      duration: '5:34'
    },
    {
      title: 'Smoking in Bed',
      description: '',
      url: 'https://youtu.be/jGD3FjrbvYA',
      duration: '3:55'
    },
    {
      title: 'Move To Mississippi',
      url: 'https://youtu.be/WmZWowVBcrA',
      duration: '5:38'
    },
    {
      title: 'Where the Big Girls At',
      description: '',
      url: 'https://youtu.be/1vD1-DvSK9w',
      duration: '5:21'
    }
  ];
};

const selectedSongReducer = (selectedSong = null, action) => {
  if (action.type === 'SONG_SELECTED') {
    return action.payload;
  }

  return selectedSong;
};

export default combineReducers({
  songs: songsReducer,
  selectedSong: selectedSongReducer
});
