import React from 'react';
import youtube_parser from './data/youtube_parser';

const VideoDetail = ({ video }) => {
  if (!video) {
    return <div>Loading...</div>;
  }
  const videoSrc = youtube_parser(video.url);
  const videoUrl = `https://www.youtube.com/embed/${videoSrc}`;

  return (
    <div className="screen">
      <div className="ui embed">
        <iframe title="video player" src={videoUrl} />
      </div>
      <div className="ui segment">
        <h4 className="ui header">{video.title}</h4>
        <p>{video.description}</p>
      </div>
    </div>
  );
};

export default VideoDetail;
