export const youtube_parser = (url) => {
  var regExp =
    /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
  var match = url.match(regExp);
  return match && match[7].length == 11 ? match[7] : false;
};


export const get_youtube_iframe = (url, width, height) => {
  const youtube_id = youtube_parser(url)

  result = `${<iframe title="YouTube video player" width="#{width}"
              height="#{height}" src="//www.youtube.com/embed/#{youtube_id}?autoplay=0&rel=0&color=white&enable=jsapi=0&showinfo=0&allowfullscreen"></iframe>}`;
}
